<?phpdefined( '_JEXEC' ) or die();// --- big data - запросим из таблицы данные о том, какие товары посещали пользователи вместе с этим// отсортируем по наиболее посещаемым и выдадим листинг$q ="SELECT *FROM #__z_roistat_dopWHERE product_from={$product->product_id} OR product_to={$product->product_id}ORDER BY popular DESC";$db->setQuery($q);if(!$big_data = $db->loadObjectList())    return;$data = null;foreach($big_data AS $a){    if(!isset($data[$a->product_from]))        $data[$a->product_from] = 0;    if(!isset($data[$a->product_to]))        $data[$a->product_to] = 0;    $data[$a->product_from] += $a->popular;    $data[$a->product_to] += $a->popular;}$prods = null;$q = "-1";$i=0;foreach($data AS $prod_id=>$popular){    if($prod_id==$product->product_id)        continue;    $q .= ", {$prod_id} ";    if($i++>10)        break;}$q ="SELECT p.`name_ru-RU` title, p.product_id, p.sklad, p.product_price, p.product_old_price, p.product_ean, p.image, p.average_rating, c.category_id, p.label_id, lab.name label_nameFROM #__jshopping_products AS pLEFT JOIN #__jshopping_products_to_categories AS c ON c.product_id=p.product_idLEFT JOIN #__jshopping_product_labels AS lab ON lab.id=p.label_idWHEREp.product_id IN (".$q.") ANDp.product_publish=1 AND p.product_price>0 AND (p.sklad=0 OR p.sklad=1 OR p.sklad=4)ORDER BY p.hits DESCLIMIT 10";$db->setQuery($q);$products = $db->loadObjectList();?><section class="b-section__like">    <div class="container">        <h2 class="b-section__title b-section__title--notLink">Вам также может понравится</h2>        <nav class="b-slider">            <ul class="b-slider__list" data-max="5" data-width="220">                <?php                $temp = $product;                foreach($products AS $product)                {                    echo include(JPATH_ROOT.'/exe/product_in_slider.php');                }                $product = $temp;                ?>            </ul>            <div class="b-slider__nav b-slider__nav--left"></div>           