2) && ($prod->product_old_price*1.0>0) )   // --- акция                    {                        $old_price = "&nbsp;&nbsp;<span>" . echo_price($prod->product_old_price, -1 ,-1 , $prod) . "</span>";                    }                    ?>                    <li class="slider-product__slide">                        <div class="slider-product__card">                            <div class="slider-product__img">                                <img rel='<?php echo $img; ?>' alt="" src="https://piano.by/images/temp.png" class="postloader_src" />                            </div>                            <div class="slider-product__inf">                                <a href="<?php echo $link; ?>" class="slider-product__title"><?php echo $prod->title; ?></a>                            <?php /*<div class="b-slider__productRate">                                    <span style="width:<?php echo $rating; ?>%"></span>                                </div>*/?>                                <p><?php echo ((($prod->sklad!=3)&&($prod->product_price>0))?echo_price($prod->product_price,-1,-1, $prod):'') . $old_price; ?>&nbsp;</p>                            </div>                        </div>                    </li>                    <?php                }            // if(sizeof($products)<4)            //     for($i=0;$i<4-sizeof($products);$i++)            //         echo '<li class="b-slider__item">';            ?>        </ul>    </div></section><?php } ?>